import React from 'react'

class TaskForm extends React.Component {

    state = {
        task: "",
        category: ""
    }

    changeHandler = (e) => {
        e.persist()
        this.setState(() => ({ task: e.target.value }))
    }

    shiftCategories = () => {
        this.props.categories.shift()
        return this.props.categories.map(category => <option>{category}</option>)
    }

    selectChangeHandler = (e) => {
        e.persist()
        console.log(e.target.value)
        this.setState(() => ({ category: e.target.value }))
    }

    submitHandler = (e) => {
        e.preventDefault()
        this.props.appSubmitHandler(this.state)
    }

    render() {
        console.log(this.state)
        return (
            <form className="new-task-form" onSubmit={this.submitHandler} >
                <input type="text" placeholder="New task details" value={this.state.task} onChange={this.changeHandler} />
                <select onChange={this.selectChangeHandler}>
                    {this.shiftCategories()}
                </select>
                <input type="submit" value="Add task" />
            </form>
        )
    }
}

export default TaskForm
