import React from 'react'

class CategoryButton extends React.Component {

    filterTasks = () => {
        return this.props.tasks.filter(task => task.category === this.props.category)
    }

    sendSelected = () => {
        this.props.getSelected
    }

    changeSelected = () => {
        this.setState(() => ({ selected: "selected" }))
    }

    render() {
        return <button className={this.state.selected} onClick={this.changeSelected} > {this.props.category}</button >
    }
}

export default CategoryButton
