import React from 'react'

function filterTasks(props) {
    return props.tasks.filter(task => task.category === props.category)
}

function renderTasks(props) {
    filterTasks(props).map(task => <TaskDiv text={task.text} label={task.category} />)
}

function Tasks(props) {
    return (
        < div className="tasks" >
            <h5>Tasks</h5>
            <div>
                {renderTasks(props)}
            </div>
        </div>
    )
}

export default Tasks
